    def __init__(self, query):
        self.query = query

    def find(self):
        return [x for x in Entities.DATA if self.query in x[3] or self.query == x[0] or self.query == str(x[1])]

    def results(self):
        results = self.find()
        rlen = len(results)
        if rlen == 0:
            return Entities.to_alfred([dict(arg='', title='No results', subtitle='Try a different keyword', icon='')])
        else:
            return Entities.to_alfred([dict(arg=x[0], title='&amp;%s; - &amp;#%s; - &amp;#x%s; - \\%s' % (x[0], x[1], hex(x[1]).replace('0x', ''), hex(x[1]).replace('0x', '').rjust(4, '0')), subtitle=x[2], icon='fonts/%s.png' % x[1]) for x in results])

    @staticmethod
    def to_alfred(items):
        result = '<?xml version="1.0"?>\n<items>\n'
        for item in items:
            result += '<item arg="%s" valid="yes"><title>%s</title><subtitle>%s</subtitle><icon>%s</icon></item>\n' % (item['arg'], item['title'], item['subtitle'], item['icon'])
        result += '</items>'
        return result

    @staticmethod
    def details(code):
        entity = [x for x in Entities.DATA if code == x[0]][0]
        subtitle = 'Press Enter to paste, or Cmd+Enter to copy'
        to_hex = hex(entity[1]).replace('0x', '')
        result = [
            dict(arg='&#%s;' % entity[1], title='&#%s; - character' % entity[1], subtitle=subtitle, icon='fonts/%s.png' % entity[1]),
            dict(arg='&amp;%s;' % entity[0], title='&amp;%s; - named entity' % entity[0], subtitle=subtitle, icon='fonts/%s.png' % entity[1]),
            dict(arg='&amp;#%s;' % entity[1], title='&amp;#%s; - decimal entity' % entity[1], subtitle=subtitle, icon='fonts/%s.png' % entity[1]),
            dict(arg='&amp;#x%s;' % to_hex, title='&amp;#x%s; - hexadecimal entity' % to_hex, subtitle=subtitle, icon='fonts/%s.png' % entity[1]),
            dict(arg='\\%s' % to_hex.rjust(4, '0'), title='\\%s - octal entity (for CSS content)' % to_hex.rjust(4, '0'), subtitle=subtitle, icon='fonts/%s.png' % entity[1])
        ]
        return Entities.to_alfred(result)
